function timeago() {
    $('[datetime]:not(.timeago)').each(function() {
        var $this = $(this);
        var data_timeago = $this.attr('datetime');
        $this.addClass('timeago');
        if (!$this.attr('title')) {
            $this.attr('title', data_timeago);
        }
        $this.text(get_timeago(data_timeago));
    });

    function get_timeago(t) {
        var $ago = 'ago';
        var $seconds = 'seconds';
        var $minutes = 'minutes';
        var $hours = 'hours';
        var $days = 'days';
        var $months = 'months';
        var $years = 'years';

        if($_config['local']['country_id'] == 'id-ID') {
            $ago = 'yang lalu';
            $seconds = 'detik';
            $minutes = 'menit';
            $hours = 'jam';
            $days = 'hari';
            $months = 'bulan';
            $years = 'tahun';
        }
        var e = new Date(t),
            a = 36e5,
            o = 24 * a,
            s = 30 * o,
            i = 365 * o,
            n = $ago,
            l = new Date - e;
        return l < 6e4 ? Math.round(l / 1e3) + " "+$seconds+" " + n : l < a ? Math.round(l / 6e4) + " "+$minutes+" " + n : l < o ? Math.round(l / a) + " "+$hours+" " + n : l < s ? Math.round(l / o) + " "+$days+" " + n : l < i ? Math.round(l / s) + " "+$months+" " + n : Math.round(l / i) + " "+$years+" " + n
    }
    $('#comments .datetime a:not(.timeago)').each(function() {
        var $this = $(this);
        var data_timeago = $this.text();
        $this.attr('datetime', data_timeago);

        var datetime = $this.attr('datetime');

        $this.addClass('timeago');
        $this.attr('title', datetime);

        $this.text(get_timeago(datetime));
    });
}

function separator(angka, symbol = '') {
    var separator = '';
    var angkarev = angka.toString().split('').reverse().join('');
    for (var i = 0; i < angkarev.length; i++)
        if (i % 3 == 0) separator += angkarev.substr(i, 3) + '.';
    if (symbol) {
        return symbol + ' ' + separator.split('', separator.length - 1).reverse().join('');
    } else {
        return separator.split('', separator.length - 1).reverse().join('');
    }
}

