"use strict";
  $(document).ready(function() {

    config.countdown ? $('#countDown').prop('checked', true) : $('#countDown').prop('checked', false) ;

    $('#passbtn').on('click', function(e) {
      var $this = $(this);
        if($this.hasClass('btn-dark')){
          $this.removeClass('btn-dark').addClass('btn-primary');
          $('#passinput').removeAttr('disabled');
        } else {
          $this.removeClass('btn-success').addClass('btn-dark');
          $('#passinput').attr('disabled', 'disabled')[0].value = '';
        }
      e.preventDefault();
    });

    $('#safelink').on('submit', function(e) {
      e.preventDefault();
      var $this = $(this),
      passinput = $('#passinput')[0],
      keyit = passinput.value.length ? passinput.value : config.defaultkey,
      blog = config.url.length ? config.url : window.location.protocol + "//" + window.location.hostname,
      url = $this.find('#urlinput')[0],
      randPost = $('#randPost')[0],
      result = $('#result')[0],
      data = {};
      data.url = url.value,
      data.countdown = $('#countDown')[0].checked;

      if (url.value.length) {
        if (randPost.checked) {
          if (validurlit(url.value)) {
            $.ajax({
              url: '/feeds/posts/summary?alt=json&max-results=99',
              type: 'GET',
              dataType: 'json',
              cache: true,
              beforeSend: function() {
                result.innerHTML = '<div class="text-center"><span class="spinner-border spinner-border-sm text-primary" role="status" aria-hidden="true"></span> Fetch Post</div>' ;
              },
              success: function(a){
                var post = a.feed.entry,
                    randNum = Math.floor(Math.random() * post.length);
                result.innerHTML = '<div class="alert alert-success text-center">'+config.lang.convertsuccess+'</div><div class="input-group mb-0"><div class="input-group-prepend"><button type="button" onclick="clickToCopy()" onmouseout="outFunc()" class="input-group-text" data-tooltip="tooltip" data-toggle="tooltip" title="Click to Copy Link"><i class="fas fa-copy fa-fw"></i></button></div><input id="url-result" class="form-control" onfocus="this.select()" onmouseup="return false" onclick="clickToCopy()" style="box-shadow: 0 0 0 0 transparent" value="'+post[randNum].link[4].href+'?u='+encodeURIComponent(CryptoJS.AES.encrypt(JSON.stringify(data), keyit))+'"/></div>';
                setTimeout(function(){
                  result.parentNode.style.height = result.offsetHeight+'px';
                }, 0);
              }
            });
            // result.innerHTML = '<div class="text-center"><span class="spinner-border spinner-border-sm text-primary" role="status" aria-hidden="true"></span> Fetch Post</div>' ;
          } else {
            result.innerHTML = '<div class="alert alert-warning text-center">'+config.lang.validtext+'</div>' ;
          }
        } else {
          result.innerHTML = validurlit(url.value) ? '<div class="alert alert-success text-center">'+config.lang.convertsuccess+'</div><div class="input-group mb-0"><div class="input-group-prepend"><button type="button" onclick="clickToCopy()" onmouseout="outFunc()" class="input-group-text" data-tooltip="tooltip" data-toggle="tooltip" title="Click to Copy Link"><i class="fas fa-copy fa-fw"></i></button></div><input id="url-result" class="form-control" onfocus="this.select()" onmouseup="return false" onclick="clickToCopy()" style="box-shadow: 0 0 0 0 transparent" value="'+blog+'/'+config.page+'?u='+encodeURIComponent(CryptoJS.AES.encrypt(JSON.stringify(data), keyit))+'"/></div>' : '<div class="alert alert-warning text-center">'+config.lang.validtext+'</div>' ;
        }
      } else {
        result.innerHTML = '<div class="alert alert-danger text-center">'+config.lang.urlempty+'</div>';
      }


      setTimeout(function(){
        result.parentNode.style.height = result.offsetHeight+'px';
      }, 0);
    });

  });

body.script:
  $(document).ready(function() {
    if (config.fixednavbar) {
      var navbar = $('.navbar'), placeholder = navbar.parent();
      $(window).on('resize load', function(){
        navbar.css({position: 'fixed', width: placeholder.outerWidth()+'px', zIndex: '9999999'});
        placeholder.css({height: navbar.outerHeight()+'px'});
      });
    }
  });
function clickToCopy() {
  $("#url-result").attr('readonly', true);
  var copyText = document.getElementById("url-result");
  copyText.select();
  document.execCommand("copy");
  $('.copysuccess').show("slow",);
  setTimeout( function(){ 
      // Do something after 1 second 
  $('.copysuccess').hide("slow",);
    }  , 2000 );
  }
